<script>
import FooterImages from "@/components/Footer";
import Modal from "@/components/Modal";
import Player from "@vimeo/player";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import cinEvent from "@/components/CinEvent";
import json from "~/assets/voorvertoningen.json";

export default {
  components: {
    Modal,
    FooterImages,
    cinEvent,
  },
  data() {
    return {
      title: "Voorvertoning",
      showModal: false,
      cinEvents: json,
    };
  },
  computed: {
    cinEventsSorted: function () {
      // `this` points to the vm instance
      return this.cinEvents.sort((a, b) => a.city.localeCompare(b.city));
    },
  },

  head() {
    return {
      title: this.title,
    };
  },
  mounted() {
    let iframe = document.getElementById("test");
    let player = new Player(iframe);

    ScrollTrigger.create({
      trigger: ".video-wrapper",
      start: "top center",
      end: "bottom center",
      // markers: true,
      toggleClass: { targets: ".video-wrapper", className: "active" },
      onEnter: () => player.play(),
      onEnterBack: () => player.play(),
      onLeave: () => player.pause(),
      onLeaveBack: () => player.pause(),
    });

    const tl = this.gsap
      .timeline({
        scrollTrigger: {
          trigger: ".hero",
          scrub: true,
          // markers: true,
          start: "top top",
          end: "bottom top",
        },
      })

      .to(".overlay", {
        opacity: 1,
        ease: "none",
      });
  },
};
</script>
